import React, {useEffect,useState} from 'react';

import CardList from './CardList';
import Card from "./Card";

const Products = ({products, sortProducts, addToCart}) =>  {

    const [value, setValue] = useState('Select');
    const [productsList,setProductsList] = useState([]);
    let templateList=[];
    let template = {        "id":"",
        "title": "",
        "url": "",
        "price": 0,
        "quantity": 0}

    const setList = (e) => {
        setValue(e.target.value);
        sortProducts(e.target.value);
    }

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then(async response => {
                const data = await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response statusText
                    const error = (data && data.message) || response.statusText;
                    return Promise.reject(error);
                }
                for(let i = 0 ; i < data.length ; i++){
                    template["id"] = data[i].id;
                    template["title"] = data[i].title;
                    template["url"] = data[i].image;
                    template["price"] = data[i].price;
                    template["quantity"] = 0;


                    templateList.push(template);

                }
                setProductsList(templateList);

                console.log(productsList);



            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }, []);

    return (
        <div className="products">

            <div className="products-nav">
                <h3>Products</h3>
                <div className="sort-list">
                    Sort by&nbsp;: &nbsp;
                    <select value={value} onChange={setList}>
                        <option value="Select">Select</option>
                        <option value="Highest to Lowest">Highest to Lowest</option>
                        <option value="Lowest to Highest">Lowest to Highest</option>
                    </select>
                </div>
            </div>

            <CardList products={products} addToCart={addToCart} />

        </div>
    )
}

export default Products;
