import React from 'react'

const Sizes = ({selectedSizes, setSize , totalItems,sum}) =>  {

    return (
        <div className="sizes">
            <h3>Shopping info.</h3>
            <p>shipping value (steady up to 4 items):<span>{totalItems === 0 ? "0$" :totalItems < 4 ? " 10$" : " 20$"}</span></p>
            <p>Number of items:<span>{" "+totalItems}</span></p>
            <p>Total price:<span>{" "+sum.toFixed(2)+"$"}</span></p>
            <p><b>*click on the three lines on top right in order to edit cart*</b></p>
        </div>
    )
}

export default Sizes;
